import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // CSS dosyanız

// Havaalanı isimleri ve lokasyon bilgileri
const airportNames = {
  "SPC": { name: "La Palma Airport", city: "Santa Cruz de la Palma", country: "Spain" },
  "LIS": { name: "Lisbon Humberto Delgado Airport", city: "Lisbon", country: "Portugal" },
  "ATH": { name: "Athens International Airport", city: "Athens", country: "Greece" },
  "AGP": { name: "Malaga-Costa del Sol Airport", city: "Malaga", country: "Spain" },
  "BCN": { name: "Barcelona-El Prat Airport", city: "Barcelona", country: "Spain" },
  "LPA": { name: "Gran Canaria Airport", city: "Las Palmas", country: "Spain" },
  "AOK": { name: "Karpathos Island National Airport", city: "Karpathos", country: "Greece" },
  "ALC": { name: "Alicante-Elche Airport", city: "Alicante", country: "Spain" },
  "RHO": { name: "Rhodes International Airport", city: "Rhodes", country: "Greece" },
  "FUE": { name: "Fuerteventura Airport", city: "Fuerteventura", country: "Spain" },
  "FAO": { name: "Faro Airport", city: "Faro", country: "Portugal" },
  "NBE": { name: "Enfidha-Hammamet International Airport", city: "Enfidha", country: "Tunisia" },
  "HER": { name: "Heraklion International Airport", city: "Heraklion", country: "Greece" },
  "CTA": { name: "Catania-Fontanarossa Airport", city: "Catania", country: "Italy" },
  "DLM": { name: "Dalaman Airport", city: "Dalaman", country: "Turkey" },
  "BJV": { name: "Milas-Bodrum Airport", city: "Bodrum", country: "Turkey" },
  "OPO": { name: "Francisco Sá Carneiro Airport", city: "Porto", country: "Portugal" },
  "NCE": { name: "Nice Côte d'Azur Airport", city: "Nice", country: "France" },
  "PFO": { name: "Paphos International Airport", city: "Paphos", country: "Cyprus" },
  "BRI": { name: "Bari Karol Wojtyła Airport", city: "Bari", country: "Italy" },
  "IBZ": { name: "Ibiza Airport", city: "Ibiza", country: "Spain" },
  "MJT": { name: "Mytilene International Airport", city: "Lesbos", country: "Greece" },
  "DSS": { name: "Blaise Diagne International Airport", city: "Dakar", country: "Senegal" },
  "SID": { name: "Amílcar Cabral International Airport", city: "Sal", country: "Cape Verde" },
  "ZTH": { name: "Zakynthos International Airport", city: "Zakynthos", country: "Greece" },
  "PDL": { name: "João Paulo II Airport", city: "Ponta Delgada, Azores", country: "Portugal" },
  "TFS": { name: "Tenerife South Airport", city: "Tenerife", country: "Spain" },
  "RTM": { name: "Rotterdam The Hague Airport", city: "Rotterdam", country: "Netherlands" },
  "TBS": { name: "Tbilisi International Airport", city: "Tbilisi", country: "Georgia" },
  "KGS": { name: "Kos International Airport", city: "Kos", country: "Greece" },
  "KEF": { name: "Keflavik International Airport", city: "Reykjavik", country: "Iceland" },
  "PMI": { name: "Palma de Mallorca Airport", city: "Palma", country: "Spain" },
  "VLC": { name: "Valencia Airport", city: "Valencia", country: "Spain" },
  "VCP": { name: "Viracopos-Campinas International Airport", city: "Campinas", country: "Brazil" },
  "LHR": { name: "London Heathrow Airport", city: "London", country: "United Kingdom" },
  "LCA": { name: "Larnaca International Airport", city: "Larnaca", country: "Cyprus" },
  "SVQ": { name: "Seville Airport", city: "Seville", country: "Spain" },
  "RAK": { name: "Marrakesh Menara Airport", city: "Marrakesh", country: "Morocco" },
  "AYT": { name: "Antalya Airport", city: "Antalya", country: "Turkey" },
  "ADB": { name: "Izmir Adnan Menderes Airport", city: "Izmir", country: "Turkey" },
  "GRO": { name: "Girona-Costa Brava Airport", city: "Girona", country: "Spain" },
  "SPU": { name: "Split Airport", city: "Split", country: "Croatia" }
};

const airplaneNames = {
  "OR": "Transavia Airlines",
  "HV": "Transavia Airlines",
  "KL": "KLM Royal Dutch Airlines",
  "DL": "Delta Air Lines",
  "CD": "Corendon Airlines"
};


function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState('');

  const fetchFlights = async (date) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/public-flights/flights', {
        headers: {
          'app_id': 'e4419061',  
          'app_key': '9d9c09c6bae74ae6fa9c04ad2756bcd5', 
          'ResourceVersion': 'v4',
          'Accept': 'application/json'
        },
        params: {
          scheduleDate: date,
          includedelays: false,
          page: 0,
          sort: '+scheduleTime'
        }
      });

      console.log('Uçuş verisi:', response.data);
      setFlights(response.data.flights);
    } catch (error) {
      console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
      setError('Verileri alırken bir hata oluştu.');
    }
    setLoading(false);
  };

  const handleDateChange = (event) => {
    const selectedDate = event.target.value;
    setSelectedDate(selectedDate);
    if (selectedDate) {
      fetchFlights(selectedDate);
    }
  };

  return (
    <div className="container">
      <h1>Schiphol Uçuş Bilgileri</h1>

      <div className="date-picker">
        <label htmlFor="flight-date">Uçuş Tarihini Seçin:</label>
        <input
          type="date"
          id="flight-date"
          value={selectedDate}
          onChange={handleDateChange}
        />
      </div>

      {loading && <div>Uçuş bilgileri yükleniyor...</div>}
      {error && <div>{error}</div>}

      {!loading && !error && flights.length > 0 && (
        <div className="flight-cards">
          {flights.map((flight) => (
            <div className="flight-cards">
            {flights.map((flight) => {
              const departureIATA = flight.route?.destinations[0];
              const arrivalIATA = flight.route?.destinations[1];
              const airlineCode = flight.prefixIATA;

              return (
                <div className="flight-card" key={flight.id}>
                  <div class="firstColumn">
                  <h2>
                  {departureIATA 
                      ? `${airportNames[departureIATA]?.city || 'Kalkış Bilinmiyor'}` 
                      : 'Kalkış Bilinmiyor'}
                    {arrivalIATA 
                      ? ` - ${airportNames[arrivalIATA]?.city || 'Varış Bilinmiyor'}` 
                      : ' - Varış Bilgisi Yok'}
                  </h2>
                  <div class="departureText">
                    <img src="departure.svg" alt="departure icon" class="departureicon"></img>
                    <strong>Kalkış</strong>
                  </div>
                  <strong class="flyTime">{new Date(flight.scheduleDateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</strong>
                  <p class="airlineName"><strong>Havayolu:</strong> {airplaneNames[airlineCode] || 'Bilinmiyor'}</p>
                  </div>
                  <div class="secondColumn">

                  </div>
                </div>
              );
            })}
          </div>
          ))}
        </div>
      )}

      {!loading && !error && flights.length === 0 && selectedDate && (
        <div>Seçilen tarihte uçuş bulunamadı.</div>
      )}

      {!selectedDate && (
        <div>Lütfen bir tarih seçin.</div>
      )}
    </div>
  );
}

export default App;
