import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // CSS dosyanız

// Havaalanı isimleri ve lokasyon bilgileri
const airportNames = {
  // Daha önce verdiğiniz havaalanı bilgilerini buraya ekleyin
};

function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [departureAirport, setDepartureAirport] = useState('');
  const [arrivalAirport, setArrivalAirport] = useState('');
  const [selectedDate, setSelectedDate] = useState('');

  const fetchFlights = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/public-flights/flights', {
        headers: {
          'app_id': '73e4650c',
          'app_key': '8eba139a4630c7bdc926c56b85b17c2c',
          'ResourceVersion': 'v4',
          'Accept': 'application/json'
        },
        params: {
          scheduleDate: selectedDate,
          departureAirport: departureAirport,
          arrivalAirport: arrivalAirport,
          includedelays: false,
          page: 0,
          sort: '+scheduleTime'
        }
      });

      console.log('Uçuş verisi:', response.data);
      setFlights(response.data.flights);
    } catch (error) {
      console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
      setError('Verileri alırken bir hata oluştu.');
    }
    setLoading(false);
  };

  const handleSearch = () => {
    if (selectedDate && departureAirport && arrivalAirport) {
      fetchFlights();
    } else {
      setError('Lütfen tüm bilgileri doldurun.');
    }
  };

  return (
    <div className="container">
      <h1>Uçuş Bilgileri Arama</h1>

      <div className="search-form">
        <div className="form-group">
          <label htmlFor="departure-airport">Kalkış Havaalanı:</label>
          <input
            type="text"
            id="departure-airport"
            value={departureAirport}
            onChange={(e) => setDepartureAirport(e.target.value)}
            placeholder="Örnek: LHR"
          />
        </div>
        <div className="form-group">
          <label htmlFor="arrival-airport">Varış Havaalanı:</label>
          <input
            type="text"
            id="arrival-airport"
            value={arrivalAirport}
            onChange={(e) => setArrivalAirport(e.target.value)}
            placeholder="Örnek: BCN"
          />
        </div>
        <div className="form-group">
          <label htmlFor="flight-date">Uçuş Tarihi:</label>
          <input
            type="date"
            id="flight-date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
          />
        </div>
        <button onClick={handleSearch}>Uçuşları Ara</button>
      </div>

      {loading && <div>Uçuş bilgileri yükleniyor...</div>}
      {error && <div>{error}</div>}

      {!loading && !error && flights.length > 0 && (
        <div className="flight-cards">
          {flights.map((flight) => (
            <div className="flight-card" key={flight.id}>
              <h2>Uçuş: {flight.flightName} ({flight.prefixIATA})</h2>
              <p><strong>Kalkış Tarihi:</strong> {new Date(flight.scheduleDateTime).toLocaleString()}</p>
              <p><strong>Uçak Tipi:</strong> {flight.aircraftType?.iataMain || 'Bilinmiyor'}</p>
              <p><strong>Varış Zamanı:</strong> {new Date(flight.estimatedLandingTime).toLocaleString()}</p>

              {/* Kalkış ve Varış Havaalanları */}
              <p>
                <strong>Kalkış - Varış:</strong> 
                {airportNames[flight.route?.destinations[0]] 
                  ? `${airportNames[flight.route.destinations[0]].name}, ${airportNames[flight.route.destinations[0]].country}` 
                  : 'Bilinmiyor'} 
                {flight.route?.destinations[1] 
                  ? ` - ${airportNames[flight.route.destinations[1]].name}, ${airportNames[flight.route.destinations[1]].country}` 
                  : ' - Bilinmiyor'}
              </p>

              <p><strong>Uçuş Durumu:</strong> {flight.publicFlightState?.flightStates[0] || 'Bilinmiyor'}</p>
            </div>
          ))}
        </div>
      )}

      {!loading && !error && flights.length === 0 && selectedDate && (
        <div>Seçilen tarihte ve havaalanlarında uçuş bulunamadı.</div>
      )}

      {!selectedDate && !departureAirport && !arrivalAirport && (
        <div>Lütfen tüm bilgileri doldurun.</div>
      )}
    </div>
  );
}

export default App;
