import './App.css'; // CSS dosyasını projeye eklemeyi unutma!

function App() {
  // eslint-disable-next-line no-undef
  const [flights, setFlights] = useState([]);
  // eslint-disable-next-line no-undef
  const [loading, setLoading] = useState(true);

  // eslint-disable-next-line no-undef
  useEffect(() => {
    const fetchFlights = async () => {
      try {
        // eslint-disable-next-line no-undef
        const response = await axios.get('https://api.schiphol.nl/public-flights/flights', {
          headers: {
            'app_id': '73e4650c',  
            'app_key': '8eba139a4630c7bdc926c56b85b17c2c',
            'ResourceVersion': 'v4'    
          },
          params: {
            scheduleDate: '2024-09-18',  
          }
        });
        setFlights(response.data.flights);
        setLoading(false);
      } catch (error) {
        console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
        setLoading(false);
      }
    };

    fetchFlights();
  }, []);

  if (loading) {
    return <div>Uçuş bilgileri yükleniyor...</div>;
  }

  return (
    <div className="container">
      <h1>Schiphol Uçuş Bilgileri</h1>
      {flights.map((flight) => (
        <div className="flight-card" key={flight.id}>
          <h2>{flight.flightName}</h2>
          <p><strong>Havayolu:</strong> {flight.airline}</p>
          <p><strong>Kalkış:</strong> {flight.scheduleDateTime}</p>
          <p><strong>Durum:</strong> {flight.status}</p>
        </div>
      ))}
    </div>
  );
}

export default App;
