import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await axios.get('https://api.schiphol.nl/public-flights/flights', {
          headers: {
            'app_id': '73e4650c',  
            'app_key': '8eba139a4630c7bdc926c56b85b17c2c',
            'ResourceVersion': 'v4'
          },
          params: {
            scheduleDate: '2024-09-18',  // İsteğe bağlı olarak tarihi kontrol et
          }
        });
        console.log('API Yanıtı:', response.data);  // Yanıtı konsola yazdır
        setFlights(response.data.flights);
        setLoading(false);
      } catch (error) {
        console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
        setLoading(false);
      }
    };

    fetchFlights();
  }, []);

  if (loading) {
    return <div>Uçuş bilgileri yükleniyor...</div>;
  }

  return (
    <div>
      <h1>Schiphol Uçuş Bilgileri</h1>
      <ul>
        {flights.map((flight) => (
          <li key={flight.id}>
            <strong>Uçuş No:</strong> {flight.flightName} | 
            <strong>Havayolu:</strong> {flight.airline} | 
            <strong>Kalkış:</strong> {flight.scheduleDateTime} | 
            <strong>Durum:</strong> {flight.status}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
