import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FlightInfo = () => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // API'ye GET isteği yapıyoruz
    const fetchFlightData = async () => {
      try {
        const response = await axios.get('https://api.schiphol.nl/public-flights/flights', {
          headers: {
            'Accept': 'application/json',
            'app_id': '73e4650c',   // App ID
            'app_key': '8eba139a4630c7bdc926c56b85b17c2c', // App Key
            'ResourceVersion': 'v4'  // API Version
          },
          params: {
            includedelays: false,   // URL parametresi
            page: 0,                // URL parametresi
            sort: '+scheduleTime'    // URL parametresi
          }
        });

        // Başarılı API isteği sonrası uçuş bilgilerini kaydet
        setFlights(response.data.flights);
        setLoading(false);
      } catch (error) {
        console.error('API isteği hatası:', error);
        setError('Verileri alırken bir hata oluştu.');
        setLoading(false);
      }
    };

    fetchFlightData();
  }, []);

  if (loading) return <p>Uçuş bilgileri yükleniyor...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h1>Schiphol Uçuş Bilgileri</h1>
      <ul>
        {flights.map(flight => (
          <li key={flight.id}>
            {flight.flightNumber} - {flight.scheduleDate} {flight.scheduleTime}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FlightInfo;
