import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // CSS dosyanızı eklemeyi unutmayın

function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await axios.get('/public-flights/flights', { // Relative path kullanıldı
          headers: {
            'app_id': '73e4650c',  // Schiphol API'den aldığınız app_id
            'app_key': '8eba139a4630c7bdc926c56b85b17c2c', // Schiphol API'den aldığınız app_key
            'ResourceVersion': 'v4',    // API'nin versiyonu
            'Accept': 'application/json' // İsteğe uygun olarak eklenebilir
          },
          params: {
            scheduleDate: '2024-09-10',  // Uçuş bilgilerini çekmek istediğiniz tarih
            includedelays: false,        // Diğer parametreler
            page: 0,
            sort: '+scheduleTime'
          }
        });
        console.log('API Yanıtı:', response.data); // Yanıtı konsolda kontrol edin
        setFlights(response.data.flights);
        setLoading(false);
      } catch (error) {
        console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
        setError('Verileri alırken bir hata oluştu.');
        setLoading(false);
      }
    };

    fetchFlights();
  }, []);

  if (loading) {
    return <div>Uçuş bilgileri yükleniyor...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container">
      <h1>Schiphol Uçuş Bilgileri</h1>
      <div className="flight-cards">
        {flights.map((flight) => (
          <div className="flight-card" key={flight.id}>
            <h2>{flight.flightName}</h2>
            <p><strong>Havayolu:</strong> {flight.airline}</p>
            <p><strong>Kalkış:</strong> {new Date(flight.scheduleDateTime).toLocaleString()}</p>
            <p><strong>Durum:</strong> {flight.status}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
