import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // CSS dosyanız

function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState('');

  // Havaalanı kodlarını isimlerle eşleştiren nesne
  const airportNames = {
    "SPC": "La Palma Airport (Santa Cruz de la Palma, Spain)",
    "LIS": "Lisbon Humberto Delgado Airport (Lisbon, Portugal)",
    "ATH": "Athens International Airport (Athens, Greece)",
    "AGP": "Malaga-Costa del Sol Airport (Malaga, Spain)",
    "BCN": "Barcelona-El Prat Airport (Barcelona, Spain)",
    "LPA": "Gran Canaria Airport (Las Palmas, Spain)",
    "AOK": "Karpathos Island National Airport (Karpathos, Greece)",
    "ALC": "Alicante-Elche Airport (Alicante, Spain)",
    "RHO": "Rhodes International Airport (Rhodes, Greece)",
    "FUE": "Fuerteventura Airport (Fuerteventura, Spain)",
    "FAO": "Faro Airport (Faro, Portugal)",
    "NBE": "Enfidha-Hammamet International Airport (Enfidha, Tunisia)",
    "HER": "Heraklion International Airport (Heraklion, Greece)",
    "CTA": "Catania-Fontanarossa Airport (Catania, Italy)",
    "DLM": "Dalaman Airport (Dalaman, Turkey)",
    "BJV": "Milas-Bodrum Airport (Bodrum, Turkey)",
    "OPO": "Francisco Sá Carneiro Airport (Porto, Portugal)",
    "NCE": "Nice Côte d'Azur Airport (Nice, France)",
    "PFO": "Paphos International Airport (Paphos, Cyprus)",
    "BRI": "Bari Karol Wojtyła Airport (Bari, Italy)",
    "IBZ": "Ibiza Airport (Ibiza, Spain)",
    "MJT": "Mytilene International Airport (Lesbos, Greece)"
  };


  const fetchFlights = async (date) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/public-flights/flights', {
        headers: {
          'app_id': '73e4650c',  
          'app_key': '8eba139a4630c7bdc926c56b85b17c2c', 
          'ResourceVersion': 'v4',
          'Accept': 'application/json'
        },
        params: {
          scheduleDate: date,
          includedelays: false,
          page: 0,
          sort: '+scheduleTime'
        }
      });

      console.log('Uçuş verisi:', response.data);

      setFlights(response.data.flights);
    } catch (error) {
      console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
      setError('Verileri alırken bir hata oluştu.');
    }
    setLoading(false);
  };

  const handleDateChange = (event) => {
    const selectedDate = event.target.value;
    setSelectedDate(selectedDate);
    if (selectedDate) {
      fetchFlights(selectedDate);
    }
  };

  const getAirportName = (iataCode) => {
    return airportNames[iataCode] || iataCode; // Kısaltma yoksa orijinal kısaltmayı göster
  };

  return (
    <div className="container">
      <h1>Schiphol Uçuş Bilgileri</h1>

      <div className="date-picker">
        <label htmlFor="flight-date">Uçuş Tarihini Seçin:</label>
        <input
          type="date"
          id="flight-date"
          value={selectedDate}
          onChange={handleDateChange}
        />
      </div>

      {loading && <div>Uçuş bilgileri yükleniyor...</div>}
      {error && <div>{error}</div>}

      {!loading && !error && flights.length > 0 && (
        <div className="flight-cards">
          {flights.map((flight) => (
            <div className="flight-card" key={flight.id}>
              <h2>Uçuş: {flight.flightName} ({flight.prefixIATA})</h2>
              <p><strong>Kalkış Tarihi:</strong> {new Date(flight.scheduleDateTime).toLocaleString()}</p>
              <p><strong>Uçak Tipi:</strong> {flight.aircraftType?.iataMain || 'Bilinmiyor'}</p>
              <p><strong>Varış Zamanı:</strong> {new Date(flight.estimatedLandingTime).toLocaleString()}</p>

              {/* Kalkış ve Varış Havaalanları */}
              <p><strong>Kalkış Havaalanı:</strong> {getAirportName(flight.route?.destinations[0])}</p>
              <p><strong>Varış Havaalanı:</strong> {getAirportName(flight.route?.destinations[1])}</p>

              <p><strong>Uçuş Durumu:</strong> {flight.publicFlightState?.flightStates.join(', ')}</p>
              <p><strong>Terminal:</strong> {flight.terminal}</p>
            </div>
          ))}
        </div>
      )}

      {!loading && !error && flights.length === 0 && selectedDate && (
        <div>Seçilen tarihte uçuş bulunamadı.</div>
      )}

      {!selectedDate && (
        <div>Lütfen bir tarih seçin.</div>
      )}
    </div>
  );
}

export default App;
