import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // CSS dosyanız


const express = require('express');
const mongoose = require('mongoose');
const bodyParser = require('body-parser');

const app = express();
app.use(bodyParser.json()); // JSON veri alımını sağlar


const mongoURI = 'mongodb://localhost:27017/flightDB';  // MongoDB URI
mongoose.connect(mongoURI, { useNewUrlParser: true, useUnifiedTopology: true })
    .then(() => console.log('MongoDB connected'))
    .catch(err => console.log('MongoDB connection error:', err));


const flightSchema = new mongoose.Schema({
  departureIATA: String,
  arrivalIATA: String,
  airlineCode: String,
  departureTime: Date,
  landingTime: Date,
  flightDuration: String
});


const Flight = mongoose.model('Flight', flightSchema);

app.post('/api/bookFlight', async (req, res) => {
  try {
      const { departureIATA, arrivalIATA, airlineCode, departureTime, landingTime, flightDuration } = req.body;

      // Yeni uçuş kaydı oluştur ve kaydet
      const flight = new Flight({
          departureIATA,
          arrivalIATA,
          airlineCode,
          departureTime,
          landingTime,
          flightDuration
      });

      await flight.save(); // MongoDB'ye kaydet
      res.status(201).json({ message: 'Flight successfully booked and saved!' });
  } catch (error) {
      res.status(500).json({ message: 'Failed to book flight', error });
  }
});


