import axios from 'axios';
import './App.css'; // CSS dosyanız
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import './FlightsPage.css';
import { Cursor } from 'mongoose';

// Havaalanı isimleri ve lokasyon bilgileri
const airportNames = {
  "SPC": { name: "La Palma Airport", city: "Santa Cruz", country: "Spain" },
  "LIS": { name: "Lisbon Humberto Delgado Airport", city: "Lisbon", country: "Portugal" },
  "ATH": { name: "Athens International Airport", city: "Athens", country: "Greece" },
  "AGP": { name: "Malaga-Costa del Sol Airport", city: "Malaga", country: "Spain" },
  "BCN": { name: "Barcelona-El Prat Airport", city: "Barcelona", country: "Spain" },
  "LPA": { name: "Gran Canaria Airport", city: "Las Palmas", country: "Spain" },
  "AOK": { name: "Karpathos Island National Airport", city: "Karpathos", country: "Greece" },
  "ALC": { name: "Alicante-Elche Airport", city: "Alicante", country: "Spain" },
  "RHO": { name: "Rhodes International Airport", city: "Rhodes", country: "Greece" },
  "FUE": { name: "Fuerteventura Airport", city: "Fuerteventura", country: "Spain" },
  "FAO": { name: "Faro Airport", city: "Faro", country: "Portugal" },
  "NBE": { name: "Enfidha-Hammamet International Airport", city: "Enfidha", country: "Tunisia" },
  "HER": { name: "Heraklion International Airport", city: "Heraklion", country: "Greece" },
  "CTA": { name: "Catania-Fontanarossa Airport", city: "Catania", country: "Italy" },
  "DLM": { name: "Dalaman Airport", city: "Dalaman", country: "Turkey" },
  "BJV": { name: "Milas-Bodrum Airport", city: "Bodrum", country: "Turkey" },
  "OPO": { name: "Francisco Sá Carneiro Airport", city: "Porto", country: "Portugal" },
  "NCE": { name: "Nice Côte d'Azur Airport", city: "Nice", country: "France" },
  "PFO": { name: "Paphos International Airport", city: "Paphos", country: "Cyprus" },
  "BRI": { name: "Bari Karol Wojtyła Airport", city: "Bari", country: "Italy" },
  "IBZ": { name: "Ibiza Airport", city: "Ibiza", country: "Spain" },
  "MJT": { name: "Mytilene International Airport", city: "Lesbos", country: "Greece" },
  "DSS": { name: "Blaise Diagne International Airport", city: "Dakar", country: "Senegal" },
  "SID": { name: "Amílcar Cabral International Airport", city: "Sal", country: "Cape Verde" },
  "ZTH": { name: "Zakynthos International Airport", city: "Zakynthos", country: "Greece" },
  "PDL": { name: "João Paulo II Airport", city: "Azores", country: "Portugal" },
  "TFS": { name: "Tenerife South Airport", city: "Tenerife", country: "Spain" },
  "RTM": { name: "Rotterdam The Hague Airport", city: "Rotterdam", country: "Netherlands" },
  "TBS": { name: "Tbilisi International Airport", city: "Tbilisi", country: "Georgia" },
  "KGS": { name: "Kos International Airport", city: "Kos", country: "Greece" },
  "KEF": { name: "Keflavik International Airport", city: "Reykjavik", country: "Iceland" },
  "PMI": { name: "Palma de Mallorca Airport", city: "Palma", country: "Spain" },
  "VLC": { name: "Valencia Airport", city: "Valencia", country: "Spain" },
  "VCP": { name: "Viracopos-Campinas International Airport", city: "Campinas", country: "Brazil" },
  "LHR": { name: "London Heathrow Airport", city: "London", country: "United Kingdom" },
  "LCA": { name: "Larnaca International Airport", city: "Larnaca", country: "Cyprus" },
  "SVQ": { name: "Seville Airport", city: "Seville", country: "Spain" },
  "RAK": { name: "Marrakesh Menara Airport", city: "Marrakesh", country: "Morocco" },
  "AYT": { name: "Antalya Airport", city: "Antalya", country: "Turkey" },
  "ADB": { name: "Izmir Adnan Menderes Airport", city: "Izmir", country: "Turkey" },
  "GRO": { name: "Girona-Costa Brava Airport", city: "Girona", country: "Spain" },
  "SPU": { name: "Split Airport", city: "Split", country: "Croatia" }
};

const airplaneNames = {
  "OR": "Transavia",
  "HV": "Transavia",
  "KL": "KLM",
  "DL": "Delta",
  "CD": "Corendon"
};



function App() {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState('');

  const [tripType, setTripType] = useState('Round Trip');

  const fetchFlights = async (date) => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get('/public-flights/flights', {
        headers: {
          'app_id': 'e4419061',  
          'app_key': '9d9c09c6bae74ae6fa9c04ad2756bcd5', 
          'ResourceVersion': 'v4',
          'Accept': 'application/json'
        },
        params: {
          scheduleDate: date,
          includedelays: false,
          page: 0,
          sort: '+scheduleTime'
        }
      });

      console.log('Uçuş verisi:', response.data);
      setFlights(response.data.flights);
    } catch (error) {
      console.error('Uçuş bilgilerini alırken bir hata oluştu:', error);
      setError('Verileri alırken bir hata oluştu.');
    }
    setLoading(false);
  };

  const navigate = useNavigate();

  const handleDateChange = (event) => {
    const selectedDate = event.target.value;
    setSelectedDate(selectedDate);
    if (selectedDate) {
      fetchFlights(selectedDate);
    }
  };

  const calculateFlightDuration = (departureTime, landingTime) => {
    const departureDate = new Date(departureTime);
    const landingDate = new Date(landingTime);

    const durationMs = landingDate - departureDate;
    const durationMinutes = Math.floor(durationMs / (1000 * 60));
    const hours = Math.floor(durationMinutes / 60);
    const minutes = durationMinutes % 60;

    return `${-hours} saat ${minutes} dakika`;
  };

  const handleBookFlight = async (flight) => {
    try {
      const response = await axios.post('http://localhost:3001/book-flight', {
        departureIATA: flight.route?.destinations[0],
        arrivalIATA: flight.route?.destinations[1],
        airlineCode: flight.prefixIATA,
        scheduleDateTime: flight.scheduleDateTime,
        landingTime: flight.actualLandingTime || flight.estimatedLandingTime,
        flightDuration: calculateFlightDuration(flight.scheduleDateTime, flight.actualLandingTime || flight.estimatedLandingTime),
        price: 125,
        flightName: flight.flightName,
        flightNumber: flight.flightNumber,
        prefixIATA: flight.prefixIATA
      });
      console.log('API Yanıtı:', response.data); // Yanıtı konsola yazdır
      alert(response.data);
    } catch (error) {
      console.error('Uçuşu kaydederken hata:', error.response ? error.response.data : error);
      alert('Uçuş kaydedilemedi.');
    }
  };

  return (
    <div className="container">
      <div class="firstRow">
        <div class="firstRowFirst">
          <img src="plane-airport.svg" alt="plane icon" class="planeicon"></img>
          <p>Plane Scape</p>
        </div>
        <div class="firstRowSecond">
          <img src="deal.svg" alt="deal icon" class="dealicon"></img>
          <p>Deals</p>
          <img src="globe.svg" alt="globe icon" class="dealicon"></img>
          <p class="clickable-text" onClick={() => navigate('/flights')}>Uçuşlarım</p>

          <img src="user.svg" alt="user icon" class="dealicon"></img>
          <p>Mehmet ALAN</p>
        </div>
      </div>
      <div class="firstColumn">
        <div class="searchContentRow">
          <div class="titleRow">
            <img src="plane.svg" alt="plane icon" class="planeicon"></img>
            <p>Book Your Flight</p>
          </div>
          <div className="button-group">
            <button
              className={`buttonselected round-trip ${tripType === 'Round Trip' ? 'active' : ''}`}
              onClick={() => setTripType('Round Trip')}
            >
              Round Trip
            </button>
            <button
              className={`buttonselected one-way ${tripType === 'One Way' ? 'active' : ''}`}
              onClick={() => setTripType('One Way')}
            >
              One Way
            </button>
          </div>
        </div>
        <div class="inputRow">
          <div class="location-input">
            <label for="departure"></label>
            <input type="text" id="departure" name="departure" placeholder="Kalkış Yeri"></input>
          </div>
          <div class="location-input">
            <label for="arrival"></label>
            <input type="text" id="arrival" name="arrival" placeholder="Varış Yeri"></input>
          </div>
          <div class="location-input">
            <input
                type='date'
                id="flight-date"
                value={selectedDate}
                onChange={handleDateChange}
                aria-placeholder="Gidiş Tarihi"
            />
          </div>
          <div class="location-input">
            <input
                type="date"
                id="flight-date"
                value={selectedDate}
                onChange={handleDateChange}
                aria-placeholder="Gidiş Tarihi"
            />
          </div>
        </div>
        <div class="showFlights">
        <button class="showflights">Show Flights</button>
        </div>
      </div>

      {loading && <div>Uçuş bilgileri yükleniyor...</div>}
      {error && <div>{error}</div>}

      {!loading && !error && flights.length > 0 && (
        <div className="flight-cards">
          {flights.map((flight) => (
            <div className="flight-cards">
            {flights.map((flight) => {
              const departureIATA = flight.route?.destinations[0];
              const arrivalIATA = flight.route?.destinations[1];
              const airlineCode = flight.prefixIATA;
              const departureTime = flight.scheduleDateTime;
              const landingTime = flight.actualLandingTime || flight.estimatedLandingTime;
              const flightDuration = calculateFlightDuration(departureTime, landingTime);



              return (
                <div className="flight-card" key={flight.id}>
                  <div class = "cardColumn">
                    <p class = "citiesName">
                    {departureIATA 
                      ? `${airportNames[departureIATA]?.city || 'Kalkış Bilinmiyor'}` 
                      : 'Kalkış Bilinmiyor'}
                    {arrivalIATA 
                      ? ` - ${airportNames[arrivalIATA]?.city || 'Varış Bilinmiyor'}` 
                      : ' - Varış Bilgisi Yok'}
                    </p>
                    <div class = "cardRow">
                      <div class = "departureColumn">
                        <div class = "departureRow">
                          <img src='departure.svg' alt="departure icon" class = "departureicon"></img>
                          <p>Kalkış</p>
                        </div>
                        <div class="flyTime">
                          <strong>{new Date(flight.scheduleDateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</strong>
                        </div>
                        <div class="airlineName">
                          <p><strong>Havayolu: </strong> {airplaneNames[airlineCode] || 'Bilinmiyor'}</p>
                        </div>
                      </div>
                      <div class="divider"></div>
                      <div class="planeColumn">
                        <p><strong>{airplaneNames[airlineCode]}</strong></p>
                        <img src="airplane.svg" alt="airplaneicon" class="airplaneicon"></img>
                        <p>{flightDuration}</p>
                        <p>(Nonstop)</p>
                      </div>
                      <div class="divider"></div>
                      <div class="arrivalColumn">
                        <div class="arrivalRow">
                          <img src="arrival.svg" alt="arrivalicon" class="arrivalicon"></img>
                          <p>Varış</p>
                        </div>
                        <div class="flyTime">
                          <strong>{new Date(flight.actualLandingTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</strong>
                        </div>
                        <div class="airlineName">
                          <p><strong>Havayolu: </strong> {airplaneNames[airlineCode] || 'Bilinmiyor'}</p>
                        </div>
                      </div>
                    </div>
                    <div class="priceRow">
                      <div class="price">
                        <p class="pricetext">Price: $125</p>
                        <p class="traveltype">{tripType}</p>
                      </div>
                      <button class="button" onClick={() => handleBookFlight(flight)}>Book Flight</button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          ))}
        </div>
      )}

      {!loading && !error && flights.length === 0 && selectedDate && (
        <div>Seçilen tarihte uçuş bulunamadı.</div>
      )}
    </div>

  );
}

const FlightsPage = () => {
  const [flights, setFlights] = useState([]);

  useEffect(() => {
    const fetchFlights = async () => {
      try {
        const response = await fetch('http://localhost:3001/flights');
        const data = await response.json();
        setFlights(data);
      } catch (error) {
        console.error('Veri alınırken hata oluştu:', error);
      }
    };

    fetchFlights();
  }, []);

  return (
    <div className="page-container">
      <h1>Uçuşlar</h1>
      {flights.length > 0 ? (
        <div className="card-container">
          {flights.map((flight, index) => {
            const departureDateTime = new Date(flight.scheduleDateTime);
            const departureTime = departureDateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
            const landingDateTime = new Date(flight.landingTime);
            const landingTime = landingDateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

            // Havayolu ismini bul
            const airlineName = airplaneNames[flight.airlineCode] || flight.airlineCode;

            return (
              <div key={index} className="card">
                <p>deneme</p>
                <div className="column flight-card">
                  <div className="flight-times">
                    <p className="flight-info">{departureTime} - {landingTime}</p>
                  </div>

                  <div className="details-row">
                    <div className="column airline-info">
                      <p className="airline-name">{airlineName} Airlines</p>
                      <p className="flight-details">Flight Details</p>
                    </div>

                    <div className="column flight-duration">
                      <p className="non-stop">Nonstop</p>
                      <p>{flight.flightDuration || 'Bilinmiyor'}</p> {/* Uçuş süresi için varsayılan değer */}
                    </div>

                    <div className="column flight-airports">
                      <p>{flight.departureIATA} to {flight.arrivalIATA}</p>
                      <p>{flight.prefixIATA} {flight.flightNumber}</p>
                    </div>

                    {/* Fiyat Çerçevesi */}
                    <div className="column flight-price">
                      <div className="price-box">
                        <p className="price">${flight.price || 'Bilinmiyor'}</p>
                        <p className="economy">Economy</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      ) : (
        <p>Yükleniyor...</p>
      )}
    </div>
  );
};




